    static boolean isVowel(char chr){
        return (chr == 'a' || chr == 'e' || chr == 'i' || chr == 'o' || chr == 'u' || chr == 'y') ||
               (chr == 'A' || chr == 'E' || chr == 'I' || chr == 'O' || chr == 'U' || chr == 'Y');
    }
    static boolean isConsonant(char chr){
        return (chr >= 'a' && chr <= 'z' || chr >= 'A' && chr <= 'Z') && !isVowel(chr);
    }
    
